import React, {Component} from 'react';
import Catalog from '../../CATALOG.js'
import CourseButton from '../CourseButtonComponent/CourseButton';
import { Accordion } from 'react-bootstrap';
import { Card } from 'react-bootstrap';
import user from '../../UserPlans/User';
import user2 from '../../UserPlans/User2';

class Requirement extends Component {

    constructor(props) {
        super(props);
        this.state = {
            isClicked: this.props.isClicked,
            courses: this.props.courseData.filter(a => a.Requirement === this.props.requirementTitle),
            completedCourses: [],
            shouldUpdate: this.props.shouldUpdate,
            selectedPlan: this.props.selectedPlan
        };
        this.swapIsClicked = this.swapIsClicked.bind(this);
        this.checkCompletedCourses = this.checkCompletedCourses.bind(this);
        this.componentWillReceiveProps = this.componentWillReceiveProps.bind(this)
    } 

    componentWillReceiveProps(nextProps) {
        this.setState({ 
            semDates: nextProps.semDates,
            isClicked: nextProps.isClicked,
            courses: nextProps.courseData.filter(a => a.Requirement === nextProps.requirementTitle),
            shouldUpdate: nextProps.shouldUpdate,
            selectedPlan: nextProps.selectedPlan})  
      }

    checkCompletedCourses() {
        console.log(this.state.selectedPlan)
        let userSemCopy = this.state.selectedPlan.semesters.slice();
        let userCourses = [].concat.apply([], userSemCopy);
        let Courses = this.props.courseData.filter(a => a.Requirement === this.props.requirementTitle);
        let compCourse = [];
        let coursesCopy = Courses.slice();
        for (let i = Courses.length -1 ; i >= 0; i--) {
            for (let j = userCourses.length -1; j >= 0; j--) {
                if(Courses[i]['Course'] === userCourses[j]['Course']) {
                    compCourse.push(userCourses[j]);
                    coursesCopy.splice(coursesCopy.indexOf(Courses[i]['Course']));
                }
            }
        }
        this.setState({
            courses: coursesCopy,
            completedCourses: compCourse
        })
        this.props.progress();
        this.props.onUpdate();
    }

    swapIsClicked() {
        if(this.state.isClicked === true) {
            this.setState({isClicked: false})
        } else {
            this.setState({isClicked: true})
        }
    }

    render() {
        let userSemCopy = this.state.selectedPlan.semesters.slice();
        let userCourses = [].concat.apply([], userSemCopy);
        let Courses = this.props.courseData.filter(a => a.Requirement === this.props.requirementTitle);
        let compCourse = [];
        let coursesCopy = Courses.slice();

        for (let i = 0; i < Courses.length; i++) {
            for (let j = 0; j < userCourses.length; j++) {
                if(Courses[i]['Course'] === userCourses[j]['Course']) {
                    compCourse.push(userCourses[j])
                    let correctInt;
                    for (let k = 0; k < coursesCopy.length; k++) {
                        if (coursesCopy[k]['Course'] === Courses[i]['Course']) {
                            correctInt = k;
                        }
                    }
                    coursesCopy.splice(correctInt,1);
                }
            }
        }


        // for (let i = 0; i < Courses.length; i++) {
        //     for (let j = 0; j < this.state.selectedPlanCourses.length; j++) {
        //         if (Courses[i]['Course'] === this.state.selectedPlanCourses[j]['Course']) {
        //             compCourse.push(Courses[i]);
        //             Courses.splice(i, 1);
        //             if (Courses.length === 1) {
        //                 continue;
        //             }
        //             if (Courses.length === 0) {
        //                 break;
        //             }
        //         }
        //     }
        // }

        return (
            <div>
                <Card>
                    <Accordion.Toggle as={Card.Header} eventKey={this.props.index}>
                        {compCourse.length}/{this.props.numClasses} - {this.props.requirementTitle}
                    </Accordion.Toggle>
                    {compCourse.map((course, index)=> (
                        <Accordion.Collapse key = {this.state.selectedPlan + this.props.requirementTitle + index} eventKey={this.props.index}>
                            <Card.Body className = 'CompletedCourseInfo'>
                                {course['Course']}: {course['Units']} Units
                            </Card.Body>
                        </Accordion.Collapse>
                    ))}
                    {coursesCopy.map((course, index)=> (
                        <Accordion.Collapse key = {this.state.selectedPlan + course + index} eventKey={this.props.index}>
                            <CourseButton
                                Course={course}
                                courseNumber={course['Course']}
                                courseTitle={course['Title']}
                                courseUnits={course['Units']} 
                                key = {course['Title']}
                                handleDrop={() => this.checkCompletedCourses()}/>
                        </Accordion.Collapse>
                        ))
                        }
                </Card>
            </div>
            )
    } 
}

