import React, {Component} from 'react';
import CategoryList from '../CategoryListComponent/CategoryList'
import FourYearPlan from '../FourYearPlan/FourYearPlan'
import user from '../../UserPlans/User.js'
import user2 from '../../UserPlans/User2.js'
import {Card, Nav, Collapse} from 'react-bootstrap'
import { isUserWhitespacable } from '@babel/types';

class Planner extends Component {

    constructor() {
        super();
        this.state = {
            plans: {user: user,
                     user2: user2},
            planNames: {user: 'Data Science Plan',
                        user2: 'Second Plan'},
            selectedPlan: 'user',
            shouldUpdate: false
        }
        this.onDelete = this.onDelete.bind(this);
        this.onUpdate = this.onUpdate.bind(this);
        this.changePlan = this.changePlan.bind(this);
    }
    onDelete() {
        // gets called when a course is deleted in a semester,
        // should update is then passed down to requirement so it knows to rerender the course that was deleted
        this.setState({
            shouldUpdate: true
        })
    }

    onUpdate() {
        // When course is rerendered, calls this method, changes shouldUpdate to false so it stops updating
        this.setState({
            shouldUpdate: false
        })
    }

    changePlan(plan) {
        this.setState({
            selectedPlan: plan
        })
    }

    render() {
        return (
            <div className = "Planner">
                <div className = "CourseContainer">
                    <CategoryList 
                    //['College of Letters and Sciences', 'Data Science', 'Energy Engineering]
                    categories = {this.state.plans[this.state.selectedPlan].collegeNames.concat(this.state.plans[this.state.selectedPlan].majorNames, this.state.plans[this.state.selectedPlan].minorNames)}
                    //Concatenates all college, major, minor data
                    data = {this.state.plans[this.state.selectedPlan].collegeData.concat(this.state.plans[this.state.selectedPlan].majorData, this.state.plans[this.state.selectedPlan].minorData)}
                    onUpdate = {() => this.onUpdate()}
                    shouldUpdate = {this.state.shouldUpdate}
                    progress = {this.state.plans[this.state.selectedPlan].totalProgress()}
                    selectedPlan = {this.state.plans[this.state.selectedPlan]}/>
                </div>
                <Card className = 'PlanNav' bg = 'primary'>
                    <Card.Header>
                        <Nav variant = 'tabs'>
                            {Object.keys(this.state.plans).map((plan, index) => 
                            <Nav.Item
                            className = 'PlanOptionItem'
                            style = {(this.state.selectedPlan === plan)? {backgroundColor: '#5fa4ff'} : {}}>
                                <Nav.Link
                                style = {(this.state.selectedPlan === plan)? {fontWeight: 500, color: 'white'} : {color: 'white'}}
                                className = 'PlanOption' 
                                onClick = {() => this.changePlan(plan)}
                                key = {this.state.planNames[plan]}>
                                {this.state.planNames[plan]}
                                </Nav.Link>
                            </Nav.Item>
                            )}
                        </Nav>
                    </Card.Header>
                    <Card.Body className = 'PlanCardBody'>
                        <FourYearPlan
                            semDates = {generateDates(this.state.plans[this.state.selectedPlan].startSem, this.state.plans[this.state.selectedPlan].endSem)} 
                            onDelete = {() => this.onDelete()}
                            selectedPlan = {this.state.plans[this.state.selectedPlan]}/>
                    </Card.Body>
                </Card>
            </div>
        ) 
    }
}

export default Planner;

function generateDates(start, end) {
    let listDates = [start];
    const splitStart = start.split(" ");
    let currSeason =  splitStart[0];
    let currYear = parseInt(splitStart[1], 10);
    const splitEnd = end.split(" ");
    const endSeason = splitEnd[0];
    const endYear = parseInt(splitEnd[1], 10);
    var i = 0;
    while (i < 20) {
        if (currSeason === (endSeason) && currYear === endYear) {
            break;
        }
        if (currSeason === 'Spring') {
            currSeason = 'Fall';
        } else {
            currSeason = 'Spring';
            currYear++;
        }
        listDates.push(currSeason + ' ' + currYear.toString());
        i++;
    }
    return listDates
  }
