import React, {Component} from 'react';
import Division from '../DivisionComponent/Division'; 
import Requirement from '../RequirementComponent/Requirement';
import {Accordion, Navbar, Nav, Form, FormControl, Button} from 'react-bootstrap';
import {Icon} from 'semantic-ui-react'
import courses from '../../courses.json'
import coursesTEST from '../../coursesTEST.json'
import CourseButton from '../CourseButtonComponent/CourseButton';

const allCourses = Object.keys(coursesTEST);

class CourseSearchTable extends Component {

    constructor(props) {
        super(props);

        this.state = {
            selectedCourse: ""
        }

        this.setSelectedCourse = this.setSelectedCourse.bind(this);
    }

    setSelectedCourse(course) {
        if (this.state.selectedCourse === course) {
        this.setState({
            selectedCourse: ""
        })}
        else {
            this.setState({
                selectedCourse: course
            })}
    }

    render() {
        return (
            <div>
                <Navbar bg = "primary" variant = 'dark' className="SearchCourses">
                    <button onClick = {()=>this.showDivs()}>Show Divisions</button>
                    <Form inline>
                        <FormControl type='text' placeholder = "search for a course..." className = 'SearchBar'/>
                    </Form>
                </Navbar>
                <div className="RequirementTable">
                    <Accordion className = 'CourseSearchList'>
                        {allCourses.map((course, index) => {
                        return (
                                <CourseButton
                                Course={coursesTEST[course]}
                                courseNumber={coursesTEST[course]['Course']}
                                courseTitle={coursesTEST[course]['Title']}
                                courseUnits={coursesTEST[course]['Units']} 
                                key = {coursesTEST[course]['Title']}
                                courseDescription = {coursesTEST[course]['Desc']}/>
                        )})}
                    </Accordion>
                </div>
            </div>
        )
    }
}

export default CourseSearchTable;
