import React, {Component} from 'react';
import { DragSource } from 'react-dnd';
import { Card, Collapse } from 'react-bootstrap';
import { findDOMNode } from 'react-dom';
import { Accordion, AccordionToggle, AccordionCollapse } from 'react-bootstrap';

const courseButtonSource = {
    beginDrag(props) {
        console.log('dragging')
        return props.Course;
        /* returns course in the form of: { 'Division: ...,
                                            'Requirement': ...,
                                            'Course': { 'Course': COMPSCI C100,
                                                        'Units': ...,
                                                        'Title: ...,
                                                        'Desc': ... }}]*/
    },

    endDrag(props, monitor, component) {
        return props.handleDrop();
    }
}

function collect(connect, monitor) {
    return {
        connectDragSource: connect.dragSource(),
        connectDragPreview: connect.dragPreview(),
        isDragging: monitor.isDragging(), 
    }
}

class CourseButton extends Component {
    render() {
        const { isDragging, connectDragSource, Course } = this.props;
        const opacity = isDragging ? {opacity: .5} : {};

        return (
            <Card ref={instance => {
                const node = findDOMNode(instance);
                connectDragSource(node);
                }}
                onClick = {()=> this.props.handleClick()}>
                <Card.Header className="CourseButton">{this.props.courseNumber}: {this.props.courseTitle} ({this.props.courseUnits} units)</Card.Header>
                {/* {this.props.isSelected &&  */}
                <Collapse in={this.props.isSelected}>
                <Card.Body className='CourseDescription'>
                     {this.props.courseDescription}
                </Card.Body>
                </Collapse>
            </Card>
        )
    }
}

    export default DragSource("Course", courseButtonSource, collect)(CourseButton);
